  /**
   * Execute the brain atlas search
   *
   * @param input - Validated search parameters
   * @returns List of brain atlases with pagination
   */
  async execute(
    input: z.infer<typeof BrainAtlasGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponseBrainAtlasRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;

    // Prepare query parameters - ensure page_size is always included
    const searchParams = new URLSearchParams();
    searchParams.set('page_size', String(input.page_size));

    Object.entries(input).forEach(([key, value]) => {
      if (value !== undefined && key !== 'page_size') {
        if (Array.isArray(value)) {
          value.forEach((v) => searchParams.append(key, String(v)));
        } else {
          searchParams.set(key, String(value));
        }
      }
    });

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/brain-atlas`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams,
          headers,
        })
        .json();

      // Validate response with Zod schema
      const validated = zListResponseBrainAtlasRead.parse(data);
      return validated;
    } catch (error) {
      if (error instanceof HTTPError) {
        const { status, statusText } = error.response;
        let responseBody = '';
        try {
          responseBody = await error.response.text();
        } catch {
          // Ignore if we can't read the body
        }
        throw new Error(
          `The The brain atlas endpoint returned a non 200 response code. Error: ${status} ${statusText}${responseBody ? ': ' + responseBody : ''}`
        );
      }
      throw error;
    }
  }

