/**
 * Circuit GetAll Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_circuit_getall.py
 *
 * Searches the entitycore database to retrieve circuits.
 */

import { z } from 'zod';
import { HTTPError } from 'ky';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadManyCircuitGetData,
  zListResponseCircuitRead,
} from '../autogenerated_types/entitycore/zod.gen';

/**
 * Input schema for Circuit GetAll tool
 *
 * Extends the autogenerated query schema with custom overrides:
 * - Excludes brain_region name-based parameters (EntitycoreExcludeBRParams pattern)
 * - Restricts page_size to 1-10 (default 5) instead of default 100
 * - Sets within_brain_region_direction to specific enum with default
 * - Adds within_brain_region_brain_region_id as a top-level parameter
 *
 * Equivalent to Python's CircuitGetAllInput which extends
 * EntitycoreExcludeBRParams and ReadManyCircuitGetParametersQuery
 */
const CircuitGetAllInputSchema = zReadManyCircuitGetData.shape.query
  .unwrap() // Remove optional wrapper
  .extend({
    // Override page_size with stricter constraints
    page_size: z
      .number()
      .int()
      .gte(1)
      .lte(10)
      .default(5)
      .describe('Number of items per page (1-10)'),

    // Override within_brain_region_direction with specific enum and default
    within_brain_region_direction: z
      .enum(['ascendants', 'descendants', 'ascendants_and_descendants'])
      .default('ascendants_and_descendants')
      .describe(
        'Controls whether to search in parent regions (ascendants), child regions (descendants), or both when filtering by brain region.'
      ),

    // Add within_brain_region_brain_region_id as a top-level parameter
    within_brain_region_brain_region_id: z
      .string()
      .uuid()
      .optional()
      .describe('ID of the brain region of interest in UUID format.'),
  })
  .omit({
    // Exclude brain_region name-based parameters (EntitycoreExcludeBRParams pattern)
    brain_region__name: true,
    brain_region__name__in: true,
    brain_region__name__ilike: true,
  });

/**
 * Circuit GetAll Tool
 *
 * Searches and retrieves circuits from the EntityCore knowledge graph.
 *
 * **Important**: This tool only provides basic circuit metadata and does NOT contain detailed circuit metrics such as:
 * - Node populations (biophysical or virtual neurons)
 * - Edge populations (chemical or electrical connections)
 * - Synaptic properties and connectivity data
 * - Circuit statistics and measurements
 * - Filterable properties for analysis
 *
 * For detailed circuit analysis and metrics, use the obi-one endpoints:
 * - `obione-circuitmetrics-getone` - Get comprehensive circuit metrics and properties
 * - `obione-circuitconnectivitymetrics-getone` - Analyze connectivity patterns and synaptic connections
 */
export class CircuitGetAllTool extends BaseTool<
  typeof CircuitGetAllInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-circuit-getall';
  static readonly toolNameFrontend = 'Get All Circuits';
  static readonly toolDescription = `Searches a neuroscience based knowledge graph to retrieve circuits.

**Important**: This tool only provides basic circuit metadata and does NOT contain detailed circuit metrics such as:
- Node populations (biophysical or virtual neurons)
- Edge populations (chemical or electrical connections)
- Synaptic properties and connectivity data
- Circuit statistics and measurements
- Filterable properties for analysis

The output is a list of circuits, containing:
- The circuit ID and basic identifiers
- Core circuit information and properties
- Status and state information
- Associated metadata and relationships
- Creation and modification timestamps

For detailed circuit analysis and metrics, use the obi-one endpoints:
- \`obione-circuitmetrics-getone\` - Get comprehensive circuit metrics and properties
- \`obione-circuitconnectivitymetrics-getone\` - Analyze connectivity patterns and synaptic connections`;
  static readonly toolDescriptionFrontend = `Search and retrieve circuits. Use this tool to:
• Find circuits by various criteria
• Access basic circuit metadata

Specify optional criteria to find relevant circuits.`;
  static readonly toolUtterances = [
    'Find circuits',
    'Show me available circuits',
    'What circuits are there?',
  ];

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = CircuitGetAllInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the circuit search
   *
   * @param input - Validated search parameters
   * @returns List of circuits with pagination
   */
  async execute(
    input: z.infer<typeof CircuitGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponseCircuitRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient, entityFrontendUrl } =
      this.contextVariables;

    // Prepare query parameters
    const queryParams: Record<string, string | string[]> = {};

    // Extract within_brain_region_brain_region_id and within_brain_region_direction for special handling
    const { within_brain_region_brain_region_id, within_brain_region_direction, ...restInput } = input;

    // Add all other parameters to query (excluding defaults to match Python behavior)
    Object.entries(restInput).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams[key] = Array.isArray(value) ? value.map(String) : String(value);
      }
    });

    // Always include page_size (matches Python: query_params["page_size"] = self.input_schema.page_size)
    queryParams['page_size'] = String(input.page_size ?? 5);

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      // If within_brain_region_brain_region_id is provided, retrieve the hierarchy_id
      if (within_brain_region_brain_region_id) {
        const brainRegionUrl = `${entitycoreUrl.replace(/\/$/, '')}/brain-region/${within_brain_region_brain_region_id}`;

        const brainRegionData = await httpClient
          .get(brainRegionUrl, {
            headers,
          })
          .json<{ hierarchy_id: string }>();

        // Add hierarchy_id to query params
        queryParams['within_brain_region_hierarchy_id'] = brainRegionData.hierarchy_id;

        // Only add within_brain_region_direction when hierarchy_id is present
        // (matches Python: query_params["within_brain_region_direction"] = self.input_schema.within_brain_region_direction)
        queryParams['within_brain_region_direction'] = within_brain_region_direction ?? 'ascendants_and_descendants';
      }

      const url = `${entitycoreUrl.replace(/\/$/, '')}/circuit`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams: queryParams as any,
          headers,
        })
        .json<any>();

      // Add url_link to each circuit and clear assets/contributions
      if (data.data && Array.isArray(data.data)) {
        for (const circuit of data.data) {
          // Clear assets and contributions (matching Python behavior)
          circuit.assets = [];
          circuit.contributions = [];

          // Add the link to website
          circuit.url_link = `${entityFrontendUrl}/${circuit.id}`;
        }
      }

      // Validate response with Zod schema
      const validated = zListResponseCircuitRead.parse(data);
      return validated;
    } catch (error) {
      if (error instanceof HTTPError) {
        const { status, statusText } = error.response;
        let responseBody = '';
        try {
          responseBody = await error.response.text();
        } catch {
          // Ignore if we can't read the body
        }
        throw new Error(
          `The circuit endpoint returned a non 200 response code. Error: ${status} ${statusText}${responseBody ? ': ' + responseBody : ''}`
        );
      }
      throw error;
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
