/**
 * Contribution GetAll Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_contribution_getall.py
 *
 * Searches a neuroscience based knowledge graph to retrieve contributions.
 */

import { z } from 'zod';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadManyContributionGetData,
  zListResponseContributionRead,
} from '../autogenerated_types/entitycore.ts/zod.gen';

/**
 * Input schema for Contribution GetAll tool
 *
 * Extends the autogenerated query schema with custom overrides:
 * - Restricts page_size to 1-10 (default 5) instead of default 100
 *
 * Equivalent to Python's ContributionGetAllInput which extends
 * ReadManyContributionGetParametersQuery
 */
const ContributionGetAllInputSchema = zReadManyContributionGetData.shape.query
  .unwrap() // Remove optional wrapper
  .extend({
    // Override page_size with stricter constraints
    page_size: z
      .number()
      .int()
      .gte(1)
      .lte(10)
      .default(5)
      .describe('Number of items per page (1-10)'),
  });

/**
 * Contribution GetAll Tool
 *
 * Searches and retrieves contributions from the EntityCore knowledge graph.
 * Supports filtering by agent, role, entity, and various other criteria.
 */
export class ContributionGetAllTool extends BaseTool<
  typeof ContributionGetAllInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-contribution-getall';
  static readonly toolNameFrontend = 'Get All Contributions';
  static readonly toolDescription = `Searches a neuroscience based knowledge graph to retrieve contributions.
The output is a list of contributions, containing:
- The contribution ID
- The agent (person or organization) making the contribution
- The role of the agent
- The entity being contributed to`;
  static readonly toolDescriptionFrontend = `Search and retrieve contributions. Use this tool to:
• Find contributions by agent or role
• Access detailed contribution data

Specify optional criteria to find relevant contributions.`;
  static readonly toolUtterances = [
    'Find contributions',
    'Show me available contributions',
    'What contributions are there?',
  ];
  static readonly toolHil = false;

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = ContributionGetAllInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the contribution search
   *
   * @param input - Validated search parameters
   * @returns List of contributions with pagination
   */
  async execute(
    input: z.infer<typeof ContributionGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponseContributionRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;

    // Prepare query parameters using URLSearchParams
    const searchParams = new URLSearchParams();
    Object.entries(input).forEach(([key, value]) => {
      if (value !== undefined) {
        if (Array.isArray(value)) {
          value.forEach((v) => searchParams.append(key, String(v)));
        } else {
          searchParams.set(key, String(value));
        }
      }
    });

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/contribution`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams,
          headers,
        })
        .json();

      // Validate response with Zod schema
      const validated = zListResponseContributionRead.parse(data);
      return validated;
    } catch (error: any) {
      throw new Error(
        `The contribution endpoint returned an error: ${error.message || JSON.stringify(error)}`
      );
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
