import { z } from 'zod';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadOneContributionIdGetData,
  zContributionRead,
} from '../autogenerated_types/entitycore.ts/zod.gen';

/**
 * Input schema for Contribution GetOne tool
 *
 * Uses the autogenerated path schema for the contribution ID.
 * Equivalent to Python's ContributionGetOneInput which uses a UUID field.
 */
const ContributionGetOneInputSchema = z.object({
  contribution_id: zReadOneContributionIdGetData.shape.path.shape.id_.describe(
    'ID of the contribution of interest in UUID format.'
  ),
});

/**
 * Contribution GetOne Tool
 *
 * Retrieves detailed information about a specific contribution by its ID.
 */
export class ContributionGetOneTool extends BaseTool<
  typeof ContributionGetOneInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-contribution-getone';
  static readonly toolNameFrontend = 'Get One Contribution';
  static readonly toolDescription = `Retrieves detailed information about a specific contribution from the knowledge graph.
Requires a 'contribution_id' which is the ID of the contribution of interest as registered in the knowledge graph.
The output contains detailed information about the contribution, including:
- The contribution ID
- The agent (person or organization) making the contribution
- The role of the agent
- Creation and update dates`;
  static readonly toolDescriptionFrontend = `Get detailed information about a specific contribution. Use this tool to:
• View complete contribution details
• Access agent and role information
• Get creation and update information

Specify the contribution ID to retrieve its full details.`;
  static readonly toolUtterances = [
    'Get details for this contribution',
    'Show me information about this contribution',
    'What are the properties of this contribution?',
  ];
  static readonly toolHil = false;

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = ContributionGetOneInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the contribution retrieval
   *
   * @param input - Validated input with contribution ID
   * @returns Detailed contribution information
   */
  async execute(
    input: z.infer<typeof ContributionGetOneInputSchema>
  ): Promise<z.infer<typeof zContributionRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;
    const { contribution_id } = input;

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/contribution/${contribution_id}`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient.get(url, { headers }).json();

      // Validate response with Zod schema
      const validated = zContributionRead.parse(data);
      return validated;
    } catch (error: any) {
      throw new Error(
        `The contribution endpoint returned an error: ${error.message || JSON.stringify(error)}`
      );
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
