/**
 * Etype GetAll Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_etype_getall.py
 *
 * Searches a neuroscience based knowledge graph to retrieve e-types.
 */

import { z } from 'zod';
import { HTTPError } from 'ky';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadManyEtypeGetData,
  zListResponseAnnotationRead,
} from '../autogenerated_types/entitycore/zod.gen';

/**
 * Input schema for Etype GetAll tool
 *
 * Extends the autogenerated query schema with custom overrides:
 * - Restricts page_size to 1-10 (default 5) instead of default 100
 *
 * Equivalent to Python's EtypeGetAllInput which extends
 * ReadManyEtypeGetParametersQuery
 */
const EtypeGetAllInputSchema = zReadManyEtypeGetData.shape.query
  .unwrap() // Remove optional wrapper
  .extend({
    // Override page_size with stricter constraints
    page_size: z
      .number()
      .int()
      .gte(1)
      .lte(10)
      .default(5)
      .describe('Number of items per page (1-10)'),
  });

/**
 * Etype GetAll Tool
 *
 * Searches and retrieves e-types from the EntityCore knowledge graph.
 * Supports filtering by preferred label and ID.
 */
export class EtypeGetAllTool extends BaseTool<
  typeof EtypeGetAllInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-etype-getall';
  static readonly toolNameFrontend = 'Get All E-types';
  static readonly toolDescription = `Searches a neuroscience based knowledge graph to retrieve e-types.
The output is a list of e-types, containing:
- The e-type ID
- The e-type preferred label
- The e-type description`;
  static readonly toolDescriptionFrontend = `Search and retrieve e-types. Use this tool to:
• Find e-types by preferred label
• Access detailed e-type data

Specify optional criteria to find relevant e-types.`;
  static readonly toolUtterances = [
    'Find e-types',
    'Show me available e-types',
    'What e-types are there?',
  ];

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = EtypeGetAllInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the e-type search
   *
   * @param input - Validated search parameters
   * @returns List of e-types with pagination
   */
  async execute(
    input: z.infer<typeof EtypeGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponseAnnotationRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;

    // Prepare query parameters
    const queryParams: Record<string, string | string[]> = {};

    // Add all parameters to query
    Object.entries(input).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams[key] = Array.isArray(value) ? value.map(String) : String(value);
      }
    });

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/etype`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams: queryParams as any,
          headers,
        })
        .json<any>();

      // Validate response with Zod schema
      const validated = zListResponseAnnotationRead.parse(data);
      return validated;
    } catch (error) {
      if (error instanceof HTTPError) {
        const { status, statusText } = error.response;
        let responseBody = '';
        try {
          responseBody = await error.response.text();
        } catch {
          // Ignore if we can't read the body
        }
        throw new Error(
          `The etype endpoint returned a non 200 response code. Error: ${status} ${statusText}${responseBody ? ': ' + responseBody : ''}`
        );
      }
      throw error;
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
