/**
 * Person GetAll Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_person_getall.py
 *
 * Searches a neuroscience based knowledge graph to retrieve persons.
 */

import { z } from 'zod';
import { HTTPError } from 'ky';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadManyPersonGetData,
  zListResponsePersonRead,
} from '../autogenerated_types/entitycore/zod.gen';

/**
 * Input schema for Person GetAll tool
 *
 * Extends the autogenerated query schema with custom overrides:
 * - Restricts page_size to 1-10 (default 5) instead of default 100
 *
 * Equivalent to Python's PersonGetAllInput which extends
 * ReadManyPersonGetParametersQuery
 */
const PersonGetAllInputSchema = zReadManyPersonGetData.shape.query
  .unwrap() // Remove optional wrapper
  .extend({
    // Override page_size with stricter constraints
    page_size: z
      .number()
      .int()
      .gte(1)
      .lte(10)
      .default(5)
      .describe('Number of items per page (1-10)'),
  });

/**
 * Person GetAll Tool
 *
 * Searches and retrieves persons from the EntityCore knowledge graph.
 * Supports filtering by name, ID, type, and various other criteria.
 */
export class PersonGetAllTool extends BaseTool<
  typeof PersonGetAllInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-person-getall';
  static readonly toolNameFrontend = 'Get All Persons';
  static readonly toolDescription = `Searches a neuroscience based knowledge graph to retrieve persons.
The output is a list of persons, containing:
- The person ID
- The person's given name
- The person's family name
- The person's preferred label`;
  static readonly toolDescriptionFrontend = `Search and retrieve persons. Use this tool to:
• Find persons by name
• Access detailed person data

Specify optional criteria to find relevant persons.`;
  static readonly toolUtterances = [
    'Find persons',
    'Show me available persons',
    'What persons are there?',
  ];
  static readonly toolHil = false;

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = PersonGetAllInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the person search
   *
   * @param input - Validated search parameters
   * @returns List of persons with pagination
   */
  async execute(
    input: z.infer<typeof PersonGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponsePersonRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;

    // Prepare query parameters
    const queryParams: Record<string, string | string[]> = {};

    // Add all parameters to query
    Object.entries(input).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams[key] = Array.isArray(value) ? value.map(String) : String(value);
      }
    });

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/person`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams: queryParams as any,
          headers,
        })
        .json<any>();

      // Validate response with Zod schema
      const validated = zListResponsePersonRead.parse(data);
      return validated;
    } catch (error) {
      if (error instanceof HTTPError) {
        const { status, statusText } = error.response;
        let responseBody = '';
        try {
          responseBody = await error.response.text();
        } catch {
          // Ignore if we can't read the body
        }
        throw new Error(
          `The person endpoint returned a non 200 response code. Error: ${status} ${statusText}${responseBody ? ': ' + responseBody : ''}`
        );
      }
      throw error;
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
