  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-simulationcampaign-getone';
  static readonly toolNameFrontend = 'Get One Simulation Campaign';
  static readonly toolDescription = `Retrieves detailed information about a specific simulation-campaign from the knowledge graph.
Requires a 'simulation_campaign_id' which is the ID of the simulation-campaign of interest as registered in the knowledge graph.
The output contains detailed information about the simulation-campaign, including:
- The simulation campaign ID and basic identifiers
- Core simulation campaign information and properties
- Simulation parameters and configuration
- Creation and modification timestamps
- Creation and update dates`;
  static readonly toolDescriptionFrontend = `Get detailed information about a specific simulation-campaign. Use this tool to:
• View complete simulation-campaign details
• Access detailed simulation-campaign information
• Get creation and update information

Specify the simulation-campaign ID to retrieve its full details.`;
  static readonly toolUtterances = [
    'Get details for this simulation campaign',
    'Show me information about this simulation campaign',
    'What are the properties of this simulation campaign?',
  ];

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = SimulationCampaignGetOneInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the simulation campaign retrieval
   *
   * @param input - Validated input with simulation campaign ID
   * @returns Detailed simulation campaign information with url_link added
   */
  async execute(
    input: z.infer<typeof SimulationCampaignGetOneInputSchema>
  ): Promise<z.infer<typeof zSimulationCampaignRead> & { url_link: string }> {
    const { entitycoreUrl, vlabId, projectId, httpClient, entityFrontendUrl } =
      this.contextVariables;
    const { simulation_campaign_id } = input;

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/simulation-campaign/${simulation_campaign_id}`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient.get(url, { headers }).json<any>();

      // Add url_link field (matching Python behavior)
      data.url_link = `${entityFrontendUrl}/${data.id}`;

      // Validate response with Zod schema (extended with url_link)
      const validated = zSimulationCampaignRead.parse(data);
      return {
        ...validated,
        url_link: data.url_link,
      };
    } catch (error) {
      if (error instanceof HTTPError) {
        const { status, statusText } = error.response;
        let responseBody = '';
        try {
          responseBody = await error.response.text();
        } catch {
          // Ignore if we can't read the body
        }
        throw new Error(
          `The simulation-campaign endpoint returned a non 200 response code. Error: ${status} ${statusText}${responseBody ? ': ' + responseBody : ''}`
        );
      }
      throw error;
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
