/**
 * Simulation GetOne Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_simulation_getone.py
 *
 * Retrieves detailed information about a specific simulation from the knowledge graph.
 */

import { z } from 'zod';
import { HTTPError } from 'ky';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadOneSimulationIdGetData,
  zSimulationRead,
} from '../autogenerated_types/entitycore/zod.gen';

/**
 * Input schema for Simulation GetOne tool
 *
 * Uses the autogenerated path schema for the simulation ID.
 * Equivalent to Python's SimulationGetOneInput which uses simulation_id.
 */
const SimulationGetOneInputSchema = z.object({
  simulation_id: zReadOneSimulationIdGetData.shape.path.shape.id_.describe(
    'ID of the simulation of interest in UUID format.'
  ),
});

/**
 * Simulation GetOne Tool
 *
 * Retrieves detailed information about a specific simulation by its ID.
 */
export class SimulationGetOneTool extends BaseTool<
  typeof SimulationGetOneInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-simulation-getone';
  static readonly toolNameFrontend = 'Get One Simulation';
  static readonly toolDescription = `Retrieves detailed information about a specific simulation from the knowledge graph.
Requires a 'simulation_id' which is the ID of the simulation of interest as registered in the knowledge graph.
The output contains detailed information about the simulation, including:
- The simulation ID and basic identifiers
- Core simulation information and properties
- Simulation parameters and configuration
- Creation and modification timestamps
- Creation and update dates`;
  static readonly toolDescriptionFrontend = `Get detailed information about a specific simulation. Use this tool to:
• View complete simulation details
• Access detailed simulation information
• Get creation and update information

Specify the simulation ID to retrieve its full details.`;
  static readonly toolUtterances = [
    'Get details for this simulation',
    'Show me information about this simulation',
    'What are the properties of this simulation?',
  ];

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = SimulationGetOneInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the simulation retrieval
   *
   * @param input - Validated input with simulation ID
   * @returns Detailed simulation information
   */
  async execute(
    input: z.infer<typeof SimulationGetOneInputSchema>
  ): Promise<z.infer<typeof zSimulationRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;
    const { simulation_id } = input;

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/simulation/${simulation_id}`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient.get(url, { headers }).json();

      // Validate response with Zod schema
      const validated = zSimulationRead.parse(data);
      return validated;
    } catch (error) {
      if (error instanceof HTTPError) {
        const { status, statusText } = error.response;
        let responseBody = '';
        try {
          responseBody = await error.response.text();
        } catch {
          // Ignore if we can't read the body
        }
        throw new Error(
          `The simulation endpoint returned a non 200 response code. Error: ${status} ${statusText}${responseBody ? ': ' + responseBody : ''}`
        );
      }
      throw error;
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
