/**
 * Species GetAll Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_species_getall.py
 *
 * Searches a neuroscience based knowledge graph to retrieve species.
 */

import { z } from 'zod';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadManySpeciesGetData,
  zListResponseSpeciesRead,
} from '../autogenerated_types/entitycore.ts/zod.gen';

/**
 * Input schema for Species GetAll tool
 *
 * Extends the autogenerated query schema with custom overrides:
 * - Excludes name-based parameters (EntitycoreExcludeNameParams pattern)
 * - Restricts page_size to 1-10 (default 5) instead of default 100
 * - Adds semantic_search with custom description
 *
 * Equivalent to Python's SpeciesGetAllInput which extends
 * EntitycoreExcludeNameParams and ReadManySpeciesGetParametersQuery
 */
const SpeciesGetAllInputSchema = zReadManySpeciesGetData.shape.query
  .unwrap() // Remove optional wrapper
  .extend({
    // Override page_size with stricter constraints
    page_size: z
      .number()
      .int()
      .gte(1)
      .lte(10)
      .default(5)
      .describe('Number of items per page (1-10)'),

    // Override semantic_search with custom description
    semantic_search: z
      .string()
      .optional()
      .describe(
        "Perform semantic search to find species by their names. Enter any text related to a species name (e.g., 'Mus Musculus', 'Homo Sapiens', 'Rattus Norvegicus') and receive results ranked by semantic similarity to your query."
      ),
  })
  .omit({
    // Exclude name-based parameters (EntitycoreExcludeNameParams pattern)
    name: true,
    name__in: true,
    name__ilike: true,
  });

/**
 * Species GetAll Tool
 *
 * Searches and retrieves species from the EntityCore knowledge graph.
 * Supports filtering by various criteria and semantic search.
 */
export class SpeciesGetAllTool extends BaseTool<
  typeof SpeciesGetAllInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-species-getall';
  static readonly toolNameFrontend = 'Get All Species';
  static readonly toolDescription = `Searches a neuroscience based knowledge graph to retrieve species.
The output is a list of species, containing:
- The species ID
- The species name
- The species taxonomy ID
- Creation and update dates

Note: The \`semantic_search\` parameter will always return results even for irrelevant queries - critically evaluate whether returned species are actually related to your search terms.`;
  static readonly toolDescriptionFrontend = `Search and retrieve species. Use this tool to:
• Find species by name
• Access detailed species data

Specify optional criteria to find relevant species.`;
  static readonly toolUtterances = [
    'Find species',
    'Show me available species',
    'What species are there?',
  ];
  static readonly toolHil = false;

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = SpeciesGetAllInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the species search
   *
   * @param input - Validated search parameters
   * @returns List of species with pagination
   */
  async execute(
    input: z.infer<typeof SpeciesGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponseSpeciesRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;

    // Prepare query parameters
    const queryParams: Record<string, string | string[]> = {};

    // Add all parameters to query
    Object.entries(input).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams[key] = Array.isArray(value) ? value.map(String) : String(value);
      }
    });

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/species`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams: queryParams as any,
          headers,
        })
        .json<any>();

      // Validate response with Zod schema
      const validated = zListResponseSpeciesRead.parse(data);
      return validated;
    } catch (error: any) {
      throw new Error(
        `The species endpoint returned an error: ${error.message || JSON.stringify(error)}`
      );
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
