/**
 * Strain GetAll Tool
 *
 * Translated from backend/src/neuroagent/tools/entitycore_strain_getall.py
 *
 * Searches the entitycore database to retrieve strains.
 */

import { z } from 'zod';

import { BaseTool, type EntitycoreContextVariables } from '../base-tool';
import {
  zReadManyStrainGetData,
  zListResponseStrainRead,
} from '../autogenerated_types/entitycore.ts/zod.gen';

/**
 * Input schema for Strain GetAll tool
 *
 * Extends the autogenerated query schema with custom overrides:
 * - Excludes name-based parameters (EntitycoreExcludeNameParams pattern)
 * - Restricts page_size to 1-10 (default 5) instead of default 100
 * - Adds semantic_search with custom description
 *
 * Equivalent to Python's StrainGetAllInput which extends
 * EntitycoreExcludeNameParams and ReadManyStrainGetParametersQuery
 */
const StrainGetAllInputSchema = zReadManyStrainGetData.shape.query
  .unwrap() // Remove optional wrapper
  .extend({
    // Override page_size with stricter constraints
    page_size: z
      .number()
      .int()
      .gte(1)
      .lte(10)
      .default(5)
      .describe('Number of items per page (1-10)'),

    // Override semantic_search with custom description
    semantic_search: z
      .string()
      .optional()
      .describe(
        'Perform semantic search to find strains by their names. Enter any text related to a strain name and receive results ranked by semantic similarity to your query.'
      ),
  })
  .omit({
    // Exclude name-based parameters (EntitycoreExcludeNameParams pattern)
    name: true,
    name__in: true,
    name__ilike: true,
  });

/**
 * Strain GetAll Tool
 *
 * Searches and retrieves strains from the EntityCore knowledge graph.
 * Supports filtering by species, creation/update dates, and semantic search.
 */
export class StrainGetAllTool extends BaseTool<
  typeof StrainGetAllInputSchema,
  EntitycoreContextVariables
> {
  // Static properties (tool metadata)
  static readonly toolName = 'entitycore-strain-getall';
  static readonly toolNameFrontend = 'Get All Strains';
  static readonly toolDescription = `Searches the entitycore database to retrieve strains.
The output is a list of strains, containing:
- The strain ID
- The strain name
- The strain description
- Any additional metadata

Note: The \`semantic_search\` parameter will always return results even for irrelevant queries - critically evaluate whether returned strains are actually related to your search terms.`;
  static readonly toolDescriptionFrontend = `Search and retrieve strains. Use this tool to:
• Find strains by name
• Access detailed strain data

Specify optional criteria to find relevant strains.`;
  static readonly toolUtterances = [
    'Find strains',
    'Show me available strains',
    'What strains are there?',
  ];
  static readonly toolHil = false;

  override contextVariables: EntitycoreContextVariables;
  override inputSchema = StrainGetAllInputSchema;

  constructor(contextVariables: EntitycoreContextVariables) {
    super();
    this.contextVariables = contextVariables;
  }

  /**
   * Execute the strain search
   *
   * @param input - Validated search parameters
   * @returns List of strains with pagination
   */
  async execute(
    input: z.infer<typeof StrainGetAllInputSchema>
  ): Promise<z.infer<typeof zListResponseStrainRead>> {
    const { entitycoreUrl, vlabId, projectId, httpClient } = this.contextVariables;

    // Prepare query parameters
    const queryParams: Record<string, string | string[]> = {};

    // Add all parameters to query
    Object.entries(input).forEach(([key, value]) => {
      if (value !== undefined) {
        queryParams[key] = Array.isArray(value) ? value.map(String) : String(value);
      }
    });

    // Prepare headers
    const headers: Record<string, string> = {};
    if (vlabId) {
      headers['virtual-lab-id'] = vlabId;
    }
    if (projectId) {
      headers['project-id'] = projectId;
    }

    try {
      const url = `${entitycoreUrl.replace(/\/$/, '')}/strain`;

      // Use ky's built-in JSON parsing and error handling
      const data = await httpClient
        .get(url, {
          searchParams: queryParams as any,
          headers,
        })
        .json<any>();

      // Validate response with Zod schema
      const validated = zListResponseStrainRead.parse(data);
      return validated;
    } catch (error: any) {
      throw new Error(
        `The strain endpoint returned an error: ${error.message || JSON.stringify(error)}`
      );
    }
  }

  /**
   * Static health check method (classmethod equivalent)
   *
   * @param contextVariables - Context variables with API configuration
   * @returns True if the service is online
   */
  static async isOnline(contextVariables: EntitycoreContextVariables): Promise<boolean> {
    try {
      const { entitycoreUrl, httpClient } = contextVariables;
      const healthUrl = `${entitycoreUrl.replace(/\/$/, '')}/health`;

      await httpClient.get(healthUrl);
      return true;
    } catch {
      return false;
    }
  }
}
